import React, { createRef, Component } from 'react'

export default class todolist extends Component {
    constructor(props) {
        super(props)
        this.state = {
            list: [
                {
                    title: "学习",
                    finish: false,
                },
                {
                    title: "吃饭",
                    finish: true,
                },
                {
                    title: "喝水",
                    finish: true,
                }
            ]
        }
        this.inputToDo = createRef();
    }
    render() {
        return (
            <div>
                <header>ToDoList<input type="text" ref={this.inputToDo} onKeyUp={e => { this.addToDo(e) }}></input></header>
                <h2>已完成事项</h2>
                <hr />
                <ul>
                    {this.state.list.map((value, index) => {
                        if (value.finish) {
                            return (
                                <li >
                                    <input
                                        key={index}
                                        type="checkbox"
                                        checked={value.finish}
                                        onChange={index => { this.checkboxChange(index) }}>
                                    </input>{value.title}
                                    <button>删除</button>
                                </li>
                            )
                        }
                    })}
                </ul>
                <h2>未完成事件</h2>
                <hr />
                <ul>
                    {this.state.list.map((value, index) => {
                        if (!value.finish) {
                            return (
                                <li >
                                    <input
                                        key={index}
                                        type="checkbox"
                                        checked={value.finish}>
                                    </input>{value.title}
                                    <button>删除</button>
                                </li>
                            )
                        }
                    })}
                </ul>
            </div>
        )
    }
    addToDo(e) {
        console.log(e.keyCode);
        if (e.keyCode === 13) {
            let newList = this.state.list;
            newList.push(
                {
                    title: this.inputToDo.current.value,
                    finish: false
                }
            );
            this.setState({
                list: newList,
            });
            this.inputToDo.current.value = "";
            console.log(this.state.list);
        }
    }
    checkboxChange(index) {
        console.log(index);
    }
}
