import React, { PureComponent, createRef } from 'react'

export default class todolist extends PureComponent {
    constructor(props) {
        super(props)
        this.state = {
            list: [
                {
                    title: "学习",
                    finish: false,
                },
                {
                    title: "吃饭",
                    finish: true,
                }
            ]
        }
        this.inputToDo = createRef();
    }
    render() {
        return (
            <div>
                <header>ToDoList<input type="text" ref={this.inputToDo} onKeyUp={e => { this.addToDo(e) }}></input></header>
                <h2>已完成事项</h2>
                <hr />
                <ul>
                    {this.state.list.map((value, index) => {
                        if (value.finish) {
                            return (
                                <li>
                                    <input type="checkbox" keywords={index}></input><button>删除</button>
                                </li>
                            )
                        }
                    })}
                </ul>
                <h2>未完成事件</h2>
                <hr />
                <ul>
                    <li>
                        <input type="checkbox"></input><button>删除</button>
                    </li>
                </ul>
            </div>
        )
    }
    addToDo(e) {
        if (e.keyCode === 13) {
            newList = this.state.list;
            newList.push(
                {
                    title: this.inputToDo.current.value,
                    finish: false
                }
            );
            this.setState({
                list: newList,
            });
            this.inputToDo.current.value = "";
        }
    }
}
