import React, { PureComponent, createRef } from 'react'

export default class todolist extends PureComponent {
    constructor(props) {
        super(props)
        this.state = {
            list: [
                {
                    title: "学习",
                    finish: false,
                },
                {
                    title: "吃饭",
                    finish: true,
                }
            ]
        }
        this.inputToDo = React.createRef();
    }
    render() {
        return (
            <div>
                <header>ToDoList<input type="text" ref="this.inputToDo" onKeyUp={e => { this.addToDo() }}></input></header>
                <h2>已完成事项</h2>
                <hr />
                <ul>
                    <li>
                        <input type="checkbox"></input><button>删除</button>
                    </li>
                </ul>
                <h2>未完成事件</h2>
                <hr />
                <ul>
                    <li>
                        <input type="checkbox"></input><button>删除</button>
                    </li>
                </ul>
            </div>
        )
    }
    addToDo(e) {
        console.log(e);
    }
}
